#!/usr/bin/env python # encoding: utf-8 """ Compute first and last day of the month for a precise date. Python 3
Created by Karl Dubost on 2013-04-03. MIT License. """
import datetime
def month_range(datetime_object): """give the first and last day of the month for this day""" # getting the month first_month = datetime_object.month # getting the year first_year = datetime_object.year # Computing the first day. # It is always the first of the month first_day = datetime.datetime(year=first_year, month=first_month, day=1) # computing the last day # The last day of the month can be 28, 29, 30, 31. # So we increment to the first day of next month at midnight and remove 1 second. if first_month == 12: last_day = datetime.datetime(year=first_year, month=12, day=31, hour=23, minute=59, second=59) else: last_day = datetime.datetime(year=first_year, month=first_month+1, day=1) - datetime.timedelta(seconds=1) return first_day, last_day
def main(): # given a date string date_string = "2013-04-03T16:27:00" # convert it into a python datetime object date_object = datetime.datetime.strptime(date_string, "%Y-%m-%dT%H:%M:%S") # return a tuple being the first and last day of the month for this date print(month_range(date_object))
if __name__ == '__main__': main()

#!/usr/bin/env python # encoding: utf-8 """emlx.py
Class to parse email stored with Apple proprietary emlx format Created by Karl Dubost on 2013-03-30 Inspired by Rui Carmo — https://the.taoofmac.com/space/blog/2008/03/03/2211 MIT License"""
import email import plistlib
class Emlx(object): """An apple proprietary emlx message""" def __init__(self): super(Emlx, self).__init__() self.bytecount = 0 self.msg_data = None self.msg_plist = None
def parse(self, filename_path): """return the data structure for the current emlx file * an email object * the plist structure as a dict data structure """ with open(filename_path, "rb") as f: # extract the bytecount self.bytecount = int(f.readline().strip()) # extract the message itself. self.msg_data = email.message_from_bytes(f.read(self.bytecount)) # parsing the rest of the message aka the plist structure self.msg_plist = plistlib.readPlistFromBytes(f.read()) return self.msg_data, self.msg_plist
if __name__ == '__main__': msg = Emlx() message, plist = msg.parse('your_message.emlx') # print(message) # Access to one of the email headers print(message['subject']) # Access to the plist data print(plist)

#!/usr/bin/env python3.3
import http.server

class HTTPHandler(http.server.BaseHTTPRequestHandler):
    "A very simple server"
    def do_GET(self):
        if self.path == "/":
            self.send_response(200)
            self.send_header('Content-type', 'text/plain')
            self.send_header('Toto', 'gloubiboulga')
            self.end_headers()
            self.wfile.write(bytes('Response body\n\n', 'latin1'))

if __name__ == '__main__':
    addr = ('', 9000)
    http.server.HTTPServer(addr, HTTPHandler).serve_forever()
